import { useState,useEffect } from "react";
import DraftPost from "./DraftPost";
import "./DropDraft.css"
export default function DraftBox(props) {

  const [drafts,setDrafts] = useState([])

  useEffect(() => {
    setTimeout(() => {
      console.log("Alohaaaaasaa!");
      fetchDrafts();
      console.log(1);
    }, 0);
  }, []);

  function fetchDrafts(){
    fetch("/draft/", {
      method: "GET",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token " + props.token,
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log(data);
      });
  }

  return (
    <div class="offcanvas offcanvas-end" id="DraftBox">
      <div class="offcanvas-header">
        <h1 class="offcanvas-title">Draft-Box</h1>
        <div className="d-flex justify-content-end align-items-center">
          <button type="button" className="btn text-reset">
            <i class="bi bi-trash" id="DropDraft"></i>
          </button>
          <button
            type="button"
            class="btn-close text-reset"
            data-bs-dismiss="offcanvas"
          ></button>
        </div>
      </div>
      <hr className="w-100"></hr>
      <div class="offcanvas-body">
        <DraftPost></DraftPost>
      </div>
    </div>
  );
}
