/*
 * @Description:
 * @Author: LLNEyx
 * @Date: 2022-11-06 19:34:00
 * @LastEditors: LLNEyx
 */
import SideNav from "./components/SideNav";
import ItemListBar from "./components/ItemListBar";
import HomePageComposer from "./components/HomePageComposer";
import Intro from "./components/Intro";
import { useState,useEffect } from "react";
import Post from "./components/Post";

export default function Home(props) {
  const [posts, setPosts] = useState([]);

  const postList = posts.map((post) => (
    <Post
      id={post.id}
      key={post.id}
      title={post.title}
      time={post.time}
      comments={post.comments}
      tag={post.tag}
      watches={post.watches}
      onSkim={props.onSkim}
    ></Post>
  ));

  function addPost(post) {
    const newPost = {
      section: post.tag,
      title: post.post_title,
      id: post.id,
      watches:post.watches,
      comments:post.comments,
      time:post.last_modified      
    }; 
    setPosts([...posts, newPost]);
  }

  useEffect(() => {
    setTimeout(() => {
      console.log('Aloha!')
      fetchPosts()
    }, 550); 
  }, [])
  

  function fetchPosts() {
    fetch("/post/", {
      method: "GET",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log("asdf\n"+data)
        setPosts(data)
        console.log(posts)
      });
  }

  return (
    <div className="HomePageContent">
      <Intro mainTitle="欢迎" subTitle="这里是一行描述"></Intro>
      <div className="container-fluid row">
        <div className="col-md-3 d-flex flex-column align-items-center">
          <br></br>
          <SideNav></SideNav>
        </div>
        <div className="col-md-9 ">
          {/* <button className="btn btn-outline-primary" onClick={fetchPosts}>111</button> */}
          <div class="list-group list-group-flush ItemList">
            <br></br>
            <ItemListBar></ItemListBar>
            <br></br>

            <div href="#" class="list-group-item rounded d-flex flex-column">
              {postList}
            </div>
          </div>
        </div>
        <HomePageComposer token={props.token}></HomePageComposer>
      </div>
    </div>
  );
}
