import { useEffect, useState } from "react";
import CommentList from "./components/CommentList";
import CommentSideBar from "./components/CommentSideBar";
import Intro from "./components/Intro";
import CommentPost from "./components/CommentPost";

//todo 需要根据Post点击后 后台传过来的内容来确定Header内容
export default function PostPage(props) {
  const [comments, setComments] = useState([]);

  // const commentList = comments.map((cmt) => (
  //   <CommentPost ></CommentPost>
  // ));

  //todo如果总是刷页面 那倒也是不用
  function addComment(comment) {
    setComments([...comments, comment]);
  }

  useEffect(() => {
    setTimeout(() => {
      console.log("go deeper!");
      console.log(props.post_id);
      fetchComments();
      console.log("!!");
    }, 0);
  }, []);

  function fetchComments() {
    fetch("/post/" + props.post_id + "/", {
      method: "GET",
    })
      .then((response) => {
        console.log("response");
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log("data");
        console.log(data);
        setComments(data);
        console.log("comments");
        console.log(comments);
        // console.log(commentList)
      });
  }

  function handleReply(pk){
    fetch('/post/'+pk+'/comment',{
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Token " + props.token,
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify({
        reply_to:pk
      }),
    }).then((response)=>{
      console.log(response)
    })
  }

  function handleCollect(pk){
    fetch('/post/collect/'+pk,{
      method: "PUT",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Token " + props.token,
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify({
        stars:0
      }),
    }).then((response)=>{
      console.log(pk)
      console.log(response)
    })
  }


  return (
    <div className="PostPageContent">
      <Intro mainTitle={props.id} subTitle="分区"></Intro>
      <div className="container-fluid d-flex flex-row-reverse flex-wrap">
        <div className="col-md-3 d-flex flex-column align-items-center">
          <br></br>
          <CommentSideBar onReply={handleReply} onCollect={handleCollect} post_id={props.post_id}></CommentSideBar>
        </div>

        <div className="col-md-9 me-auto">
          <CommentList></CommentList>
        </div>
      </div>
    </div>
  );
}
