/*
 * @Description:
 * @Author: LLNEyx
 * @Date: 2022-11-06 19:34:00
 * @LastEditors: LLNEyx
 */
import SideNav from "./components/SideNav";
import ItemList from "./components/ItemList";
import HomePageComposer from "./components/HomePageComposer";
import Intro from "./components/Intro";
import { useState } from "react";

export default function Home(props) {
  const [posts, setPosts] = useState([]);

  function addPost(post) {
    const newPost = {
      section: post.section,
      title: post.title,
      description: post.description.substring(0, 15),
    };
    setPosts([...posts, newPost]);
  }

  return (
    <div className="HomePageContent">
      <Intro mainTitle="欢迎" subTitle="这里是一行描述"></Intro>
      <div className="container-fluid row">
        <div className="col-md-3 d-flex flex-column align-items-center">
          <br></br>
          <SideNav></SideNav>
        </div>
        <div className="col-md-9 ">
          <ItemList onSkim={props.onSkim}></ItemList>
        </div>
        <HomePageComposer></HomePageComposer>
      </div>
    </div>
  );
}
