/*
 * @Description:
 * @Author: LLNEyx
 * @Date: 2022-11-06 19:34:00
 * @LastEditors: LLNEyx
 */
import SideNav from "./components/SideNav";
import ItemListBar from "./components/ItemListBar";
import HomePageComposer from "./components/HomePageComposer";
import Intro from "./components/Intro";
import { useState,useEffect } from "react";
import Post from "./components/Post";

export default function Home(props) {
  

  const postList = posts.map((post) => (
    <Post
      id={post.id}
      key={post.id}
      title={post.post_title}
      time={post.last_modified}
      comments={post.comments}
      tag={post.tag}
      watches={post.watches}
      onSkim={props.onSkim}
      post_url={props.open_url}
    ></Post>
  ));

  function addPost(post) {
    // const newPost = {
    //   section: post.tag,
    //   title: post.post_title,
    //   id: post.id,
    //   watches:post.watches,
    //   comments:post.comments,
    //   time:post.last_modified      
    // }; 
    setPosts([...posts, post]);
  }

  useEffect(() => {
    setTimeout(() => {
      console.log('Aloha!')
      fetchPosts()
      console.log(1)
    }, 0); 
  }, [])
  

  function fetchPosts() {
    fetch("/post/", {
      method: "GET",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log("data")
        console.log(data)
        setPosts(data)
        console.log("posts")
        console.log(posts)
        console.log(postList)
      });
  }

  return (
    <div className="HomePageContent">
      <Intro mainTitle="HOME PAGE" subTitle="LOREM" length={posts.length}></Intro>
      <div className="container-fluid row">
        <div className="col-md-3 d-flex flex-column align-items-center">
          <br></br>
          <SideNav></SideNav>
        </div>
        <div className="col-md-9 ">
          {/* <button className="btn btn-outline-primary" onClick={fetchPosts}>111</button> */}
          <div class="list-group list-group-flush ItemList">
            <br></br>
            <ItemListBar></ItemListBar>
            <br></br>

            <div href="#" class="list-group-item rounded d-flex flex-column">
              {postList}
            </div>
          </div>
        </div>
        <HomePageComposer token={props.token}></HomePageComposer>
      </div>
    </div>
  );
}
