/*
 * @Description:
 * @Author: LLNEyx
 * @Date: 2022-10-31 20:35:19
 * @LastEditors: LLNEyx
 */
// import './App.css';
import Header from "./components/Header";
import Home from "./Home";
import PostPage from "./PostPage";
import { useState } from "react";
import HomePageComposer from "./components/HomePageComposer";
import PostPageComposer from "./components/PostPageComposer";
import LoginModal from "./components/LoginModal";
import DraftBox from "./components/DraftBox";
import NotificationBox from "./components/NotificationBox";
import Post from "./components/Post";

const FILTER_MAP = {
  all: ()=>true,
  link1: (a) => a==='1',
  link2: (a) => a==='2',
  link3: (a) => a==='3',
};

const FILTER_NAMES = Object.keys(FILTER_MAP);

function App() {
  const [inPost, setInPost] = useState(false);
  const [nowToken, setNowToken] = useState("");
  const [nowUserId, setNowUserId] = useState("");
  const [nowUserName, setNowUserName] = useState("");
  const [nowEmail, setNowEmail] = useState("");
  const [isOnline, setIsOnline] = useState(false);
  const [targetPost, setTargetPost] = useState(0);

  const [posts, setPosts] = useState([]);
  const [filter,setFilter] = useState('all')
  const postList = posts.map((post) => {
    console.log(post)
    const postObj = (
      <Post
        id={post.id}
        key={post.id}
        title={post.post_title}
        time={post.last_modified}
        comments={post.comments}
        tag={post.tag}
        watches={post.watches}
        onSkim={routeToPost}
      ></Post>
    );
    console.log(postObj)
    return postObj
  });


  function addPost(post){
    setPosts(...posts,post)
  }

  const mainContent = inPost ? (
    <PostPage post_id={targetPost} token={nowToken}></PostPage>
  ) : (
    <Home postList={postList} addPost={addPost} setPosts={setPosts} token={nowToken}></Home>
  );

  function handleSearch(newPosts) {}

  function handleLogin(response) {
    console.log("token:" + response.token);
    setNowToken(response.token);
    setNowUserId(response.user_id);
    setNowUserName(response.username);
    setNowEmail(response.email);
    setIsOnline(true);
    // console.log([nowToken,nowUserId,nowUserName,nowEmail])
  }

  function handleLogout() {
    setNowToken("");
    setNowUserId("");
    setNowUserName("");
    setNowEmail("");
    setIsOnline(false);
  }

  function routeToPost(post_id) {
    setTargetPost(post_id);
    setInPost(true);
  }

  function routeToHome() {
    setInPost(false);
  }

  return (
    <div className="App">
      <Header
        onBack={routeToHome}
        isOnline={isOnline}
        onLogout={handleLogout}
        token={nowToken}
      ></Header>
      <DraftBox></DraftBox>
      <NotificationBox></NotificationBox>
      <LoginModal onLoginSuccess={handleLogin}></LoginModal>
      {mainContent}
      <HomePageComposer addPost={addPost}></HomePageComposer>
      <PostPageComposer></PostPageComposer>
    </div>
  );
}

export default App;
