  const [inPost, setInPost] = useState(true);
  const mainContent = inPost ? <PostPage></PostPage> : <Home></Home>;
  const [nowToken,setNowToken] = useState('')
  const [nowUserId,setNowUserId] = useState('')
  const [nowUserName,setNowUserName] = useState('')
  const [nowUserEmail,setNowUserEmail] = useState('')

  function handleLogin(response){
    setNowToken(response.token);
    setNowUserId(response.user_id)
    setNowUserName(response.username)
  }

  return (
    <div className="App">
      <Header></Header>
      <LoginModal onLogin={setToken}></LoginModal>
      {mainContent}
      <HomePageComposer></HomePageComposer>
      <PostPageComposer></PostPageComposer>
    </div>
  );
}

export default App;
