import { useState } from "react";

//todo LR切换的时候没有过渡动画
export default function LoginModal(props) {
  const [isLogin, setIsLogin] = useState(true);
  const [title1, setTitle1] = useState("Login");
  const [title2, setTitle2] = useState("Register");
  const [usr, setUsr] = useState("");
  const [email, setEmail] = useState("");
  const [pwd, setPwd] = useState("");
  const [pwd2, setPwd2] = useState("");

  function clearInputs() {
    setUsr("");
    setEmail("");
    setPwd("");
    setPwd2("");
    if (!isLogin) handleChangeLR();
  }

  function handleChangeUsr(e) {
    setUsr(e.target.value);
  }

  function handleChangeEmail(e) {
    setEmail(e.target.value);
  }

  function handleChangePwd(e) {
    setPwd(e.target.value);
  }

  function handleChangePwd2(e) {
    setPwd2(e.target.value);
  }

  function handleSubmit() {
    if (isLogin) {
      
    } else {
    }

    clearInputs();
  }

  function handleChangeLR() {
    var t = title1;
    setTitle1(title2);
    setTitle2(t);
    setIsLogin(!isLogin);
  }

  return (
    <div className="modal fade" id="LoginModal">
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <button
              className="border-0 btn h4 modal-title text-dark "
              onClick={handleChangeLR}
            >
              <p></p>
              <h4>
                {title1}
                <small> &gt; {title2}</small>
              </h4>
            </button>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              onClick={clearInputs}
            ></button>
          </div>

          <div className="modal-body">
            {!isLogin ? (
              <div class="form-floating mb-3 mt-3">
                <input
                  type="text"
                  class="form-control"
                  id="usrInput"
                  placeholder="Enter username"
                  name="usr"
                  value={usr}
                  onChange={handleChangeUsr}
                />
                <label for="usrInput">username</label>
              </div>
            ) : (
              <></>
            )}

            <div class="form-floating mb-3 mt-3">
              <input
                type="text"
                class="form-control"
                id="emailInput"
                placeholder="Enter email"
                name="email"
                value={email}
                onChange={handleChangeEmail}
              />
              <label for="emailInput">Email</label>
            </div>

            <div class="form-floating mt-3 mb-3">
              <input
                type="text"
                class="form-control"
                id="pwdInput"
                placeholder="Enter password"
                name="pswd"
                value={pwd}
                onChange={handleChangePwd}
              />
              <label for="pwdInput">Password</label>
            </div>

            {!isLogin ? (
              <div class="form-floating mb-3 mt-3">
                <input
                  type="text"
                  class="form-control"
                  id="pwdInput2"
                  placeholder="Repeat pwd"
                  name="pswd2"
                  value={pwd2}
                  onChange={handleChangePwd2}
                />
                <label for="pwdInput2">repeat password</label>
              </div>
            ) : (
              <></>
            )}
          </div>

          <div className="modal-footer d-flex justify-content-center">
            <button
              type="button"
              className="btn btn-outline-success"
              data-bs-dismiss="modal"
              onClick={handleSubmit}
            >
              submit
            </button>

            <button
              type="button"
              className="btn btn-outline-secondary"
              data-bs-dismiss="modal"
              onClick={clearInputs}
            >
              cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
